import React, { Component } from 'react';
import Web3 from 'web3';
import detectEthereumProvider from '@metamask/detect-provider';
import CryptoBird from '../abis/CryptoBirdz.json';

class App extends Component {

    async componentDidMount() {
        await this.loadWeb3();
        await this.loadBlockchainData();
    }

    // first up is to detect ethereum provider
    async loadWeb3() {
        const provider = await detectEthereumProvider();

        // modern browsers
        if (provider) {
            console.log('Ethereum wallet is connected');
            window.web3 = new Web3(provider);
        } else {
            console.log('no ethereum wallet detected');
        }
    }

    async loadBlockchainData() {
        const web3 = window.web3;
        const accounts = await window.web3.eth.getAccounts();
        this.setState({ account: accounts })

        const networkId = web3.eth.net.getId();
        const networkData = CryptoBird.networks[networkId];

    }

    constructor(props) {
        super(props);
        this.state = {
            account: ''

        }
    }

    render() {
        return (
            <div>
                <nav className='navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow'>
                    <div className='navbar-brand col-sem-3 col-md-3 mr-0' style={{ color: 'white' }}>
                        Crypto Birdz NFTs (Non-Fungible Tokens)
                    </div>
                    <ul className='navbar-nav px-3'>
                        <l className='nav-item text-nowrap d-none d-sm-none d-sm-block'>
                            <small className='text-white'>
                                {this.state.account}
                            </small>
                        </l>
                    </ul>
                </nav>
            </div>
        )
    }
}

export default App;
